<script type="text/javascript">
 function get_captain(id)
  {
    var id = id;
    $('.captains_role').removeClass("roleSelected_961ad");
    $("#cap_"+id).addClass("roleSelected_961ad");
    
    $('#captain_id').val(id);

    var vicecaptain = $('#vicecaptain_id').val();

    if(id == vicecaptain)
    {
    	alert('Please Select other player');
    	$('.captains_role').removeClass("roleSelected_961ad");
    	$('#captain_id').val('');
    	$('#sub_save').addClass('disabledButton_cf79e raisedGreenButtonDisabled_e0a23');
    	$(":submit").attr("disabled", true);
    }
    else if(vicecaptain =="" || id =="")
    {
      $('#sub_save').addClass('disabledButton_cf79e raisedGreenButtonDisabled_e0a23');
      $(":submit").attr("disabled", true);
    }	


  }

  function get_vicecaptain(id)
  {
    var id = id;
    $('.vicecaptains_role').removeClass("roleSelected_961ad");
    $("#vice_"+id).addClass("roleSelected_961ad");
    $('#vicecaptain_id').val(id);

    var captain = $('#captain_id').val();
    if(id == captain)
    {
    	alert('Please Select other player');
    	$('.vicecaptains_role').removeClass("roleSelected_961ad");
    	$('#vicecaptain_id').val('');
    	$('#sub_save').addClass('disabledButton_cf79e raisedGreenButtonDisabled_e0a23');
    	$(":submit").attr("disabled", true);
    }
    else if(captain =="" || id =="")
    {
      $('#sub_save').addClass('disabledButton_cf79e raisedGreenButtonDisabled_e0a23');
      $(":submit").attr("disabled", true);
    }


  }

 function check(){

    var captain = $('#captain_id').val();
    var vicecaptain = $('#vicecaptain_id').val();

    if(captain !="" || vicecaptain !="")
    {
      $('#sub_save').removeClass('disabledButton_cf79e raisedGreenButtonDisabled_e0a23');
      $(":submit").removeAttr("disabled");
    }
    else if(captain =="" || vicecaptain =="")
    {
      $('#sub_save').addClass('disabledButton_cf79e raisedGreenButtonDisabled_e0a23');
    }
}
</script>