<script type="text/javascript">
   var  SITE_URL = "<?php echo base_url(''); ?>";
   function get_city(id) {     
    var id = id;   
    $.ajax({
        type : 'post',       
        url: SITE_URL+'/website/get_city_by_stateid',
        data : { id : id },
        success : function(data) {
            $('#city_id').html(data);
        }
    });
};
</script>